class DemandeRepository extends \Doctrine\ORM\EntityRepository
{

    public function countUnreadCol(){
        return $this->createQueryBuilder('d')
            ->select('COUNT(d)')
            ->where('d.readDemande = false')
            ->getQuery()
            ->getSingleScalarResult();
    }

    public function allDemandes(){
        return $this->createQueryBuilder('d')
            ->select('COUNT(d)')
            ->getQuery()
            ->getSingleScalarResult();
    }



    public function countDemandeParMoisClient($date,$id){
        return $this->createQueryBuilder('d')
            ->select('COUNT(d)')
            ->where( "DATE_FORMAT(d.date_emission, '%Y-%m') = :date" )
            ->andWhere("d.id_client = :id")

            ->setParameter('date',  $date )
            ->setParameter('id',  $id )

            ->getQuery()
            ->getSingleScalarResult();
    }

    public function countDemandeParMoisLivreur($date,$id){
        return $this->createQueryBuilder('d')
            ->select('COUNT(d)')
            ->where( "DATE_FORMAT(d.date_emission, '%Y-%m') = :date" )
            ->andWhere("d.id_livreur = :id")
            ->setParameter('date',  $date )
            ->setParameter('id',  $id )
            ->getQuery()
            ->getSingleScalarResult();
    }

    public function countDemandeParMois($date){
        return $this->createQueryBuilder('d')
            ->select('COUNT(d)')
            ->where( "DATE_FORMAT(d.date_emission, '%Y-%m') = :date" )
            ->setParameter('date',  $date )
            ->getQuery()
            ->getSingleScalarResult();
    }




    public function clotureAdmin(){
        return $this->createQueryBuilder('d')
            ->select('COUNT(d)')
            ->where('d.etat = ?1')
            ->setParameter(1, 'Cloture')
            ->getQuery()
            ->getSingleScalarResult();
    }


    public function cloture($id,$etat){
        return $this->createQueryBuilder('d')
            ->select('COUNT(d)')
            ->where('d.etat = :etat')
            ->setParameter('etat', $etat)
            ->setParameter('id', $id)
            ->andWhere('d.id_client = :id')
            ->getQuery()
            ->getSingleScalarResult();
    }

