    public function dixDernierDemande()
    {
        return $this->createQueryBuilder('a')
            ->select('a')
            ->orderBy("a.updated_date","DESC")
            ->getQuery()
            ->setMaxResults(10)
            ->getResult();


    }

    public function dixDernierDemandeClient($id)
    {
        return $this->createQueryBuilder('a')
            ->select('a')
            ->where("a.id_client = :id")
            ->setParameter('id',$id)
            ->orderBy("a.updated_date","DESC")
            ->getQuery()
            ->setMaxResults(10)
            ->getResult();


    }


    public function dixDernierDemandeLivreur($id)
